import { useState, useEffect } from 'react';
import { fetchElectronicsData } from './API';
import './stockCss.css';
function Stock() {
    const [arr, setArr] = useState([]);
    useEffect(() => {
        fetchElectronicsData()
            .then(res => setArr(res));
    }, []);
    const total = arr.reduce((acc, item) => {
        return acc + (item.CostPrice * item.Available);
    }, 0);

    const [searchTerm, setSearchTerm] = useState("");
    const filteredData = arr.filter(item =>
        item.name.toLowerCase().includes(searchTerm.toLowerCase())
    );

    return (
        <>
            <h1 id='deStock' className='text-center'>Dariyalal Electronics</h1>
            <div className='text-center my-4'>
                <div className="input-group" style={{ width: "450px", margin: "auto" }}>
                    <input
                        type="text"
                        className='form-control rounded-pill rounded-end border-0 bg-secondary searchBar'
                        placeholder='Search for an item...'
                        aria-label="Search"
                        value={searchTerm}
                        onChange={(e) => setSearchTerm(e.target.value)}
                    />
                    <span className="input-group-text bg-secondary border-0 rounded-start rounded-pill" id="basic-addon1">
                        <i className="bi bi-search text-white"></i>
                    </span>
                </div>
            </div>
            <div className='container d-flex justify-content-center align-items-center'>
                <div className='row'>
                    <div className='col'>
                        <table border="1" className='table table-hover table-info table-bordered text-center'>
                            <thead>
                                <tr border="2">
                                    <th id='thNO'>No.</th>
                                    <th>Item</th>
                                    <th id='thAvailable'>Available</th>
                                    <th className='price'>Cost Price</th>
                                </tr>
                            </thead>
                            <tbody>
                                {arr.map((item, index) => (
                                    <tr key={item.id}>
                                        <td>{index + 1}</td>
                                        <td>{item.Item}</td>
                                        <td>{item.Available}</td>
                                        <td>{item.CostPrice}</td>
                                    </tr>
                                ))}
                                <tr className='fs-5'>
                                    <td colSpan="2"><strong>Total</strong></td>
                                    <td colSpan="2"><strong>{total}</strong></td>
                                </tr>
                            </tbody>
                        </table>
                    </div>
                </div>
            </div>
        </>
    );
}

export default Stock;
