import './salesCss.css';
import { useState, useEffect } from 'react';
import { fetchElectronicsData } from './API';
function Sales() {
    const today = new Date();
    const year = today.getFullYear();
    const month = String(today.getMonth() + 1).padStart(2, '0');
    const day = String(today.getDate()).padStart(2, '0');
    const formattedDate = day + '-' + month + '-' + year;

    const [rows, setRows] = useState([
        {
            No: '',
            Item: '',
            CostPrice: '',
            Quantity: '',
            SellPrice: '',
            Amount: '',
            Profit: '',
            searchTerm: '',
            showSuggestions: false
        }
    ]);

    const [arr, setArr] = useState([]);
    useEffect(() => {
        fetchElectronicsData()
            .then(res => setArr(res));
    }, []);

    const handleInputChange = (index, e) => {
        const { name, value } = e.target;
        const updatedRows = [...rows];
        updatedRows[index][name] = value;

        const quantity = updatedRows[index].Quantity || 0;
        const sellPrice = updatedRows[index].SellPrice || 0;
        const costPrice = updatedRows[index].CostPrice || 0;

        updatedRows[index].Amount = sellPrice * quantity;
        updatedRows[index].Profit = sellPrice - costPrice;

        setRows(updatedRows);
    };

    const handleItemChange = (index, e) => {
        const searchTerm = e.target.value;
        const updatedRows = [...rows];
        updatedRows[index].searchTerm = searchTerm;
        updatedRows[index].showSuggestions = true;

        setRows(updatedRows);
    };

    const addRow = () => {
        if (allFieldsFilled()) {
            setRows([...rows, {
                Item: '',
                CostPrice: '',
                Quantity: '',
                SellPrice: '',
                Amount: '',
                Profit: '',
                isInvalid: false,
                searchTerm: '',
                showSuggestions: false,
            }]);
        }
        else {
            const updatedRows = [...rows];
            updatedRows[rows.length - 1].isInvalid = true;
            setRows(updatedRows);
        }
    };

    const allFieldsFilled = () => {
        const lastRow = rows[rows.length - 1];
        return lastRow.Item && lastRow.Quantity && lastRow.SellPrice;
    };

    const handleDeleteRow = (index) => {
        const updatedRows = rows.filter((_, i) => i !== index);
        setRows(updatedRows);
    };

    useEffect(() => {
        const handleKeyPress = (event) => {
            if (event.key === 'Enter') {
                event.preventDefault();
                addRow();
            }
        };
        window.addEventListener('keydown', handleKeyPress);
        return () => {
            window.removeEventListener('keydown', handleKeyPress);
        };
    }, []);

    const [searchTerm, setSearchTerm] = useState("");
    const [showSuggestions, setShowSuggestions] = useState(false);
    const suggestions = arr.filter(item =>
        item.Item.toLowerCase().startsWith(searchTerm.toLowerCase()) && searchTerm !== ""
    );

    const handleSuggestionClick = (index, suggestion) => {
        const selectedItemData = arr.find((item) => item.Item === suggestion);

        const updatedRows = [...rows];
        updatedRows[index].Item = suggestion;
        updatedRows[index].CostPrice = selectedItemData ? selectedItemData.CostPrice : '';
        updatedRows[index].searchTerm = suggestion;
        updatedRows[index].showSuggestions = false; // Hide suggestions after selection

        setRows(updatedRows);
    };

    const totalQuantity = rows.reduce((acc, row) => acc + (parseInt(row.Quantity) || 0), 0);
    const totalAmount = rows.reduce((acc, row) => acc + (parseFloat(row.Amount) || 0), 0);
    const totalProfit = rows.reduce((acc, row) => acc + (parseFloat(row.Profit) || 0), 0);

    return (
        <>
            <h1 id='deSales' className='text-center m-3'>Sales</h1>
            <div className='container-fluid px-4'>
                <div className='row my-3'>
                    <div className='col'>Date: <span className='fw-bold fst-italic'>{formattedDate}</span></div>
                </div>
                <div className='row'>
                    <div className='col table-responsive'>
                        <table className='table table-striped table-bordered text-center'>
                            <thead>
                                <tr>
                                    <th style={{ width: "3vw" }}>No.</th>
                                    <th style={{ width: "29vw" }}>Item</th>
                                    <th style={{ width: "10vw" }}>Cost Price</th>
                                    <th style={{ width: "5vw" }}>Quantity</th>
                                    <th style={{ width: "10vw" }}>Sell Price</th>
                                    <th style={{ width: "15vw" }}>Amount</th>
                                    <th style={{ width: "15vw" }}>Profit</th>
                                    <th style={{ width: "5vw" }}>Action</th>
                                </tr>
                            </thead>
                            <tbody className='align-middle'>
                                {rows.map((row, index) => (
                                    <tr key={index}>
                                        <td>
                                            {index + 1}
                                        </td>
                                        <td>
    <div style={{ position: 'relative' }}>
        <input
            type="text"
            className='form-control rounded-pill'
            value={row.searchTerm}
            onChange={(e) => handleItemChange(index, e)}
            onBlur={() => setTimeout(() => handleInputChange(index, { target: { name: 'showSuggestions', value: false } }), 200)}
            onFocus={() => handleInputChange(index, { target: { name: 'showSuggestions', value: true } })}
        />
        {row.showSuggestions && (
            <ul className="list-group position-absolute w-100" style={{ top: '100%', zIndex: 1000 }}>
                {arr.filter(item => item.Item.toLowerCase().startsWith(row.searchTerm.toLowerCase())).map((suggestion) => (
                    <li
                        key={suggestion.id}
                        className="list-group-item list-group-item-action"
                        onClick={() => handleSuggestionClick(index, suggestion.Item)}
                        style={{ cursor: "pointer" }}
                    >
                        {suggestion.Item}
                    </li>
                ))}
            </ul>
        )}
    </div>
</td>

                                        <td>
                                            {row.CostPrice}
                                        </td>
                                        <td>
                                            <input
                                                type='number'
                                                name='Quantity'
                                                value={row.Quantity}
                                                onChange={(e) => handleInputChange(index, e)}
                                                className={'form-control ' + (row.isInvalid && !row.Quantity ? "invalid-input" : "")}
                                            />
                                        </td>
                                        <td>
                                            <input
                                                type='number'
                                                name='SellPrice'
                                                value={row.SellPrice}
                                                onChange={(e) => handleInputChange(index, e)}
                                                className={'form-control ' + (row.isInvalid && !row.SellPrice ? "invalid-input" : "")}
                                            />
                                        </td>
                                        <td>
                                            {row.Amount}
                                        </td>
                                        <td>
                                            {row.Profit}
                                        </td>
                                        <td>
                                            <button className='btn btn-danger' onClick={() => handleDeleteRow(index)}>Delete</button>
                                        </td>
                                    </tr>
                                ))}
                                <tr className='fs-5 text-center'>
                                    <td colSpan="3" className='text-start px-3'><strong>Total:</strong></td>
                                    <td><strong>{totalQuantity}</strong></td>
                                    <td></td>
                                    <td><strong>{totalAmount.toFixed(2)}</strong></td>
                                    <td><strong>{totalProfit.toFixed(2)}</strong></td>
                                    <td></td>
                                </tr>
                            </tbody>
                        </table>
                    </div>
                </div>
                <div className='row'>
                    <div className='col text-center'>
                        <button className='btn btn-primary' onClick={addRow}>
                            Add New Row
                        </button>
                    </div>
                </div>
            </div>
        </>
    );
}

export default Sales;
