import { getBillById } from './API';
import { useState, useEffect } from 'react';
function Bill_id() {
    const [bill, setBill] = useState({ 
                                date: '', 
                                CustomerName: '', 
                                Items: [],
                                Quantiy: [],
                                SellPrice: [], 
                                Amount: 0 
                            });
    useEffect(() => {
        getBillById(1)
            .then(bill => {
                console.log(bill)
                setBill(bill)
            })
            ;
    }, []);

    return (
        <>
            <h1 className='text-center m-4' style={{ fontSize: "50px" }}>Bill</h1>
            <div className='container d-flex justify-content-center align-items-center'>
                <div className='row'>
                    <div className='col'>
                        <table border="1" className='table table-hover table-info table-bordered text-center'>
                            <thead>
                                <tr border="2">
                                    <th>No.</th>
                                    <th>Item</th>
                                    <th>Quantity</th>
                                    <th>Price</th>
                                    <th>Amount</th>
                                </tr>
                            </thead>
                            <tbody>
                            {bill.Items.length > 0 ? (
                                    bill.Items.map((item, index) => (
                                        <tr key={index}>
                                            <td>{index + 1}</td>
                                            <td>{item.Item}</td>
                                            <td>{item.Quantity}</td>
                                            <td>{item.SellPrice}</td>
                                            <td>{item.Quantity * item.SellPrice}</td>
                                        </tr>
                                    ))
                                ) : (
                                    <tr>
                                        <td colSpan="5">No items in the bill</td>
                                    </tr>
                                )}
                                {/* <tr className='fs-5'>
                                    <td colSpan="2"><strong>Total</strong></td>
                                    <td colSpan="2"><strong>{total}</strong></td>
                                </tr> */}
                            </tbody>
                        </table>
                    </div>
                </div>
            </div>
        </>
    );
}

export default Bill_id;
