const apiURL = 'https://667323296ca902ae11b33da7.mockapi.io/Electronics';
export const fetchElectronicsData=()=>{
    const arr=fetch(apiURL).then(res=>res.json());
    return arr;
}
// export const fetchElectronicsDataByName=()=>{
//     const arr=fetch(apiURL).then(res=>res.json());
//     return arr;
// }
export const addElectronics=(data,navigate)=>{
    fetch(apiURL,{
        method:"POST",
        body:JSON.stringify(data),
        headers:{
            "Content-Type":"application/json"
        }
    })
    .then(res=>res.json())
    .then(res=>{
        navigate('/stock');
    });
}
export const deleteElectronics = async (id,navigate) => {    
    fetch(apiURL+'/'+id, {method: "DELETE"})
    .then(res=>res.json())
    .then(res=>{
        navigate('/stock');
    });
};


