    .then(res=>res.json())
    .then(res=>{
        navigate('/sales');
    });
}
export const deleteBill = async (id) => {    
    fetch(apiBill+'/'+id, {method: "DELETE"})
    .then(res=>res.json())
    .then(res=>{
        navigate('/sales');
    });
};
