const apiURL = 'http://localhost:3000/stock';  // Ensure the correct URL is being used
export const fetchElectronicsData = async () => {
    try {
        const res = await fetch(apiURL);
        if (!res.ok) {
            throw new Error(`Network Response Error: ${res.statusText}`);
        }

        // Log response text for debugging
        const responseText = await res.text();
        console.log('Response Text:', responseText);  // Check what the server actually returns

        // Try to parse the response text as JSON
        try {
            const arr = JSON.parse(responseText);
            return arr;
        } catch (jsonError) {
            throw new Error('Response is not valid JSON');
        }

    } catch (error) {
        throw new Error(`API Response Error: ${error.message}`);
    }
};
