import './addStockCss.css';
import { useOutletContext } from 'react-router-dom';
import { useState,useEffect } from 'react';
function AddStock() {
    const [Available,setAvailable]=useState(0);
    const [selectedItem,setSelectedItem]=useState('');
    const {arr,error}=useOutletContext();
    const handleItemChange=(e)=>{
        const selectedItem = e.target.value;
        setSelectedItem(selectedItem);

        // Find the selected item in the array and set its available stock
        const selectedItemData = arr.find(item => item.Item === selectedItem);
        if (selectedItemData) {
            setAvailable(selectedItemData.Available || 0);  // Set available value
        } else {
            setAvailable(0);  // Default to 0 if not found
        }
    };
    useEffect(() => {
        if (selectedItem) {
            console.log(selectedItem);
        }
    }, [selectedItem]);
    return (
        <>
            <div className='container text-center'>
                <div className='row'>
                    <div className='col'>
                        <h1 id='addDE'>Modify your Stock!</h1>
                    </div>
                </div>
                <div className='row justify-content-center'>
                    <div className='col-6'>
                        <form id='addStock'>
                            <div className='m-3'>
                                <label>Item: </label>
                                {error?(
                                    <p>Error loading items:{error}</p>
                                ):(
                                    <select value={selectedItem} onChange={handleItemChange}>
                                        <option>Select any item!</option>
                                        {arr.map((item) => (
                                            <option key={item.No}>
                                                {item.Item}
                                            </option>
                                        ))}
                                    </select>
                                )}
                            </div>
                            <div>
                                <label>Available: </label>
                                <div>{handleAvailableChange}</div>
                            </div>
                        </form>
                    </div>
                </div>
            </div>
        </>
    );
}

export default AddStock;
