import './addStockCss.css';
import {useOutletContext} from 'react-router-dom';
import {useState} from 'react';
function AddStock() {
    const [available,setAvailable]=useState(0);
    const {arr,error}=useOutletContext();
    return (
        <>
            <div className='container text-center'>
                <div className='row'>
                    <div className='col'>
                        <h1 id='addDE'>Modify your Stock!</h1>
                    </div>
                </div>
                <div className='row justify-content-center'>
                    <div className='col-6'>
                        <form id='addStock'>
                            <div className='m-3'>
                                <label>Item: </label>
                                <select>
                                    <option>Select any item!</option>
                                    {/* <option>i#0DjQbE?v</option>
                                    <option>4X=P1++ujt</option>
                                    <option>&0mDjyrIh%</option> */}
                                    {arr.map((item)=>(
                                        <option key={item.id} value={item.id}>
                                            {item.name}
                                        </option>
                                    ))}
                                </select>
                            </div>
                            <div>
                                <label>Available: </label>
                                <input type='text' />
                            </div>
                        </form>
                    </div>
                </div>
            </div>
        </>
    );
}

export default AddStock;
