import logo from './logo.svg';
import './App.css';
import {Outlet,Link} from 'react-router-dom';
import {useState,useEffect} from 'react';
import {fetchElectronicsData} from './components/API';
import Stock from './components/Stock';


function App() {
  const [arr, setArr] = useState([]);
    const [error, setError] = useState(null);
    useEffect(() => {
        const fetchData = async () => {
            try {
                const data=await fetchElectronicsData();
                setArr(data);
            } catch (err) {
                setError(err.message);
            }
        };
        fetchData();
    }, []);
  return (
    <>
      <nav className="navbar navbar-expand-lg bg-body-tertiary">
        <div className="container-fluid">
          <Link className="navbar-brand" to="/">DE</Link>
          <button className="navbar-toggler" type="button" data-bs-toggle="collapse" data-bs-target="#navbarSupportedContent" aria-controls="navbarSupportedContent" aria-expanded="false" aria-label="Toggle navigation">
            <span className="navbar-toggler-icon"></span>
          </button>
          <div className="collapse navbar-collapse" id="navbarSupportedContent">
            <ul className="navbar-nav me-auto mb-2 mb-lg-0">
              <li className="nav-item">
                <Link className="nav-link active" aria-current="page" to="/">Home</Link>
              </li>
              <li className="nav-item">
                <Link className="nav-link" to="/profitLoss">P/L Statement</Link>
              </li>
              <li className="nav-item">
                <Link className="nav-link" to="/stock">Stock</Link>
              </li>
              <li className="nav-item">
                <Link className="nav-link" to="/stock/add">Add Stock</Link>
              </li>
            </ul>
          </div>
        </div>
      </nav>
      <div className="App">
            {/* Only passing data to components, not rendering it here */}
            <Stock data={arr} error={error} />
        </div>
      <Outlet />
    </>
  );
}

export default App;
