import './salesCss.css';
import { useState, useEffect } from 'react';
import { useNavigate } from 'react-router-dom';
import { fetchBillData,deleteBill,getBillById } from './API';
function Sales() {

    const [arr, setArr] = useState([]);
    useEffect(() => {
        fetchBillData()
            .then(res => setArr(res))
    }, []);

    const navigate = useNavigate();

    const [rows, setRows] = useState([]);
    const addRow = () => {
        setRows((prevRows) => [
            ...prevRows,
            {Date:'', CustomerName: '', Amount: '' }
        ]);
        navigate('/sales/bill');
    };

    const handleDeleteRow = async (index) => {
        await deleteBill(index,navigate);
        setArr(arr.filter(row => row.id !== index));
    };

    const totalAmount = arr.reduce((acc, row) => acc + (parseFloat(row.Amount) || 0), 0);

    const handleRowClick = (id) => {
        navigate('/sales/bill/'+id);
    };

    return (
        <>
            <h1 className='text-center m-3'>Sales</h1>
            <div className='container-fluid px-4'>
                <div className='row'>
                    <div className='col table-responsive'>
                        <table className='table table-striped table-hover table-bordered text-center'>
                            <thead>
                                <tr>
                                    <th style={{ width: "3vw" }}>No.</th>
                                    <th style={{ width: "5vw" }}>Date</th>
                                    <th className='text-start' style={{ width: "29vw" }}>Customer</th>
                                    <th style={{ width: "10vw" }}>Amount</th>
                                    <th style={{ width: "5vw" }}>Action</th>
                                </tr>
                            </thead>
                            <tbody className='align-middle'>
                                {arr.map((row, index) => (
                                    <tr key={index}>
                                        <td>
                                            {index + 1}
                                        </td>
                                        <td>
                                            {row.date}
                                        </td>
                                        <td className='text-start'>
                                            {row.CustomerName}
                                        </td>
                                        <td>
                                            {row.Amount}
                                        </td>
                                        <td>
                                            <button className='btn btn-danger' onClick={() => handleDeleteRow(row.id)}>Delete</button>
                                        </td>
                                    </tr>
                                ))}
                                <tr className='fs-5 text-center'>
                                    <td colSpan="3" className='text-start px-3'><strong>Total:</strong></td>
                                    <td><strong>{totalAmount}</strong></td>
                                    <td></td>
                                </tr>
                            </tbody>
                        </table>
                    </div>
                </div>
                <div className='row'>
                    <div className='col text-center'>
                        <button className='btn btn-primary' onClick={addRow}>
                            Add New Bill
                        </button>
                    </div>
                </div>
            </div>
        </>
    );
}

export default Sales;
