import './style.css';
import { useState, useEffect } from 'react';
import { fetchElectronicsData } from './API';
import { deleteElectronics } from './API';
function Stock() {
    const [arr, setArr] = useState([]);
    useEffect(() => {
        fetchElectronicsData()
            .then(res => setArr(res));
    }, []);

    const [id,setId] = useState(-1);

    const [searchTerm, setSearchTerm] = useState("");

    const filteredData = arr.filter(item =>
        item.Item.toLowerCase().includes(searchTerm.toLowerCase())
    );

    const total = filteredData.reduce((acc, item) => {
        return acc + (item.CostPrice * item.Available);
    }, 0);

    const deleteButton = (id) => {
        Swal.fire({
            title: "Are you sure?",
            text: "You won't be able to revert this!",
            icon: "warning",
            showCancelButton: true,
            confirmButtonColor: "#3085d6",
            cancelButtonColor: "#d33",
            confirmButtonText: "Yes, delete it!"
          }).then((result) => {
            if (result.isConfirmed) {
              deleteElectronics(id)
              .then(() => {
                    setArr(prevArr => prevArr.filter(item => item.id !== id));
                });
            }
          });
    }

    return (
        <>
            <h1 id='deStock' className='text-center'>Dariyalal Electronics</h1>
            <div className='text-center my-4'>
                <div className="input-group" style={{ width: "450px", margin: "auto" }}>
                    <input
                        type="text"
                        className='form-control rounded-pill rounded-end border-0 bg-secondary searchBar'
                        placeholder='Search for an item...'
                        aria-label="Search"
                        style={{ color: "white", fontSize: "18px"}}
                        value={searchTerm}
                        onChange={(e) => setSearchTerm(e.target.value)}
                    />
                    <span className="input-group-text bg-secondary border-0 rounded-start rounded-pill" id="basic-addon1">
                        <i className="bi bi-search text-white"></i>
                    </span>
                </div>
            </div>
            <div className='container d-flex justify-content-center align-items-center'>
                <div className='row'>
                    <div className='col'>
                        {filteredData.length > 0 ? (
                            <table border="1" className='table table-hover table-info table-bordered text-center'>
                                <thead>
                                    <tr border="2">
                                        <th style={{width: "50px"}}>No.</th>
                                        <th style={{width: "350px"}}>Item</th>
                                        <th style={{width: "150px"}}>Available</th>
                                        <th style={{width: "200px"}}>Cost Price</th>
                                        <th style={{width: "250px"}} colSpan={2}>Activity</th>
                                    </tr>
                                </thead>
                                <tbody>
                                    {filteredData.map((item, index) => (
                                        <tr key={item.id}>
                                            <td>{index + 1}</td>
                                            <td>{item.Item}</td>
                                            <td>{item.Available}</td>
                                            <td>{item.CostPrice}</td>
                                            <td><button className='btn btn-warning'>Edit</button></td>
                                            <td><button className='btn btn-danger' onClick={() => deleteButton(item.id)}>Delete</button></td>
                                        </tr>
                                    ))}
                                    <tr className='fs-5'>
                                        <td colSpan="2"><strong>Total</strong></td>
                                        <td colSpan="2"><strong>{total}</strong></td>
                                        <td colSpan={2}></td>
                                    </tr>
                                </tbody>
                            </table>
                        ) : (
                            <p className="text-dark fs-4 fw-bold">No items found!</p>
                        )}
                    </div>
                </div>
            </div>
        </>
    );
}

export default Stock;
