import './stockCss.css';
import {useState,useEffect} from 'react';
function Stock(){
    const [arr,setArr]=useState([]);
    let apiURL='https://667323296ca902ae11b33da7.mockapi.io/Electronics';
    useEffect(()=>{
        fetch(apiURL)
            .then((res)=>{
                if(!res.ok){
                    throw new Error('Network Response Error');
                }
                return res.json();
            })
            .then((data)=>{
                console.log("API Called");
                setArr(data);
                console.log(arr);
            })
            .catch((err)=>{
                throw new Error('API Response Error');
            })
    },[]);
    return(
        <>
            <h1>Dariyalal Electronics</h1>
            <div className='container'>
                <div className='row'>
                    <div className='col'>
                    {error ? ( // display error if any
                            <p>Error: {error}</p>
                        ) : (
                            <table>
                                <thead>
                                    <tr>
                                        <th>No.</th>
                                        <th>Item</th>
                                        <th>Cost Price</th>
                                        <th>Selling Price</th>
                                        <th>Available</th>
                                    </tr>
                                </thead>
                                <tbody>
                                    {arr.map((item, index) => (
                                        <tr key={item.id}>
                                            <td>{index + 1}</td>
                                            <td>{item.name}</td>
                                            <td>{item.cost_price}</td>
                                            <td>{item.selling_price}</td>
                                            <td>{item.available}</td>
                                        </tr>
                                    ))}
                                </tbody>
                            </table>
                        )}

                    </div>
                </div>    
            </div>
        </>
    );
}

export default Stock;
