import { useState, useEffect } from 'react';
import { useNavigate } from 'react-router-dom';
import { fetchBillData, deleteBill, getBillById, renumberBills, updateElectronics } from './API';
function Sales() {

    const [arr, setArr] = useState([]);
    useEffect(() => {
        fetchBillData()
            .then(res => setArr(res))
    }, []);

    const navigate = useNavigate();

    const addRow = () => {
        const billNo = arr.length + 1;
        navigate('/sales/bill/' + billNo);
    };

    const handleDeleteRow = async (id) => {
        const billData = await getBillById(id);
        if (!billData) {
            alert('Bill not found.');
            return;
        }

        // for (const item of billData.ItemNames) {
        //     const itemName = item;
        //     const soldQuantity = parseInt(billData.Quantities[billData.ItemNames.indexOf(item)]);
        //     const stockItem = arr.find(e => e.Item === itemName);
        //     console.log(stockItem);
        //     if (stockItem) {
        //         const newStock = stockItem.Available + soldQuantity;
                
        //         const stockData = {
        //             Item: stockItem.Item,
        //             Available: newStock,
        //             CostPrice: stockItem.CostPrice
        //         };

        //         updateElectronics(stockData, stockItem.No);
        //     }
        // }
        for (let i = 0; i < billData.length; i++) {
            const itemName = billData[i].Item;
            const soldQuantity = billData[i].Quantity;

            const itemData = arr.find(item => item.Item === itemName);
            if (itemData) {
                const soldQuantity = parseInt(billData[i].Quantity) || 0;
                const stockData = {
                    Item: itemData.Item,
                    Available: newStock,
                    CostPrice: parseInt(itemData.CostPrice)
                };
                await updateElectronics(stockData, itemData.No);
            }
        }
        await deleteBill(id,navigate);
        await renumberBills(id);
        const updatedArr = await fetchBillData();
        setArr(updatedArr);
    }

    const totalAmount = arr.reduce((acc, row) => acc + (parseFloat(row.Amount) || 0), 0);

    const handleRowClick = async (id) => {
        await getBillById(id);
        navigate('/sales/' + id);
    };

    return (
        <>
            <h1 className='text-center m-4' style={{ fontSize: "50px", margin: "25px 30px 25px 30px" }}>Sales</h1>
            <div className='container-fluid px-4'>
                <div className='row'>
                    <div className='col table-responsive'>
                        <table className='table table-striped table-hover table-bordered text-center'>
                            <thead>
                                <tr>
                                    <th style={{ width: "3vw" }}>No.</th>
                                    <th style={{ width: "5vw" }}>Date</th>
                                    <th className='text-start' style={{ width: "29vw" }}>Customer</th>
                                    <th style={{ width: "10vw" }}>Amount</th>
                                    <th style={{ width: "5vw" }}>Action</th>
                                </tr>
                            </thead>
                            <tbody className='align-middle'>
                                {arr.map((row, index) => (
                                    <tr key={index} onClick={() => handleRowClick(row.No)} style={{ cursor: 'pointer' }}>
                                        <td>
                                            {index + 1}
                                        </td>
                                        <td>
                                            {row.date}
                                        </td>
                                        <td className='text-start'>
                                            {row.CustomerName}
                                        </td>
                                        <td>
                                            {row.Amount}
                                        </td>
                                        <td>
                                            <button className='btn btn-danger' onClick={(e) => {
                                                e.stopPropagation();
                                                handleDeleteRow(row.No)}
                                            }>Delete</button>
                                        </td>
                                    </tr>
                                ))}
                                <tr className='fs-5 text-center'>
                                    <td colSpan="3" className='text-start px-3'><strong>Total:</strong></td>
                                    <td><strong>{totalAmount}</strong></td>
                                    <td></td>
                                </tr>
                            </tbody>
                        </table>
                    </div>
                </div>
                <div className='row'>
                    <div className='col text-center'>
                        <button className='btn btn-primary' onClick={addRow}>
                            Add New Bill
                        </button>
                    </div>
                </div>
            </div>
        </>
    );
}

export default Sales;
