import './salesCss.css';
import { useState, useEffect, useRef } from 'react';
import { useNavigate } from 'react-router-dom';
import { fetchBillData,deleteBill } from './API';
function Sales() {

    const [arr, setArr] = useState([]);
    useEffect(() => {
        fetchBillData()
            .then(res => setArr(res))
    }, []);

    const today = new Date();
    const year = today.getFullYear();
    const month = String(today.getMonth() + 1).padStart(2, '0');
    const day = String(today.getDate()).padStart(2, '0');
    const formattedDate = day + '-' + month + '-' + year;
    const idRef = useRef(1);

    useEffect(() => {
        const savedBillData = localStorage.getItem('billData');
        if (savedBillData) {
            const billData = JSON.parse(savedBillData);
            setRows((prevRows) => [
                ...prevRows,
                { CustomerName: billData.customerName, Amount: billData.totalAmount },
            ]);
        }
    }, []);

    const navigate = useNavigate();

    const [rows, setRows] = useState([]);
    const addRow = () => {
        setRows((prevRows) => [
            ...prevRows,
            { CustomerName: '', Amount: '' }
        ]);
        navigate('/sales/'+idRef.current);
        idRef.current++; 
    };

    const handleDeleteRow = async (index) => {
        await deleteBill(index);
        const updatedRows = arr.filter((_, i) => i !== index);
        setArr(updatedRows);
    };

    const totalAmount = rows.reduce((acc, row) => acc + (parseFloat(row.Amount) || 0), 0);

    return (
        <>
            <h1 className='text-center m-3'>Sales</h1>
            <div className='container-fluid px-4'>
                <div className='row my-3'>
                    <div className='col'>Date: <span className='fw-bold fst-italic'>{formattedDate}</span></div>
                </div>
                <div className='row'>
                    <div className='col table-responsive'>
                        <table className='table table-striped table-bordered text-center'>
                            <thead>
                                <tr>
                                    <th style={{ width: "3vw" }}>No.</th>
                                    <th style={{ width: "29vw" }}>Customer</th>
                                    <th style={{ width: "10vw" }}>Amount</th>
                                    <th style={{ width: "5vw" }}>Action</th>
                                </tr>
                            </thead>
                            <tbody className='align-middle'>
                                {arr.map((row, index) => (
                                    <tr key={index}>
                                        <td>
                                            {index + 1}
                                        </td>
                                        <td>
                                            {row.CustomerName}
                                        </td>
                                        <td>
                                            {row.Amount}
                                        </td>
                                        <td>
                                            <button className='btn btn-danger' onClick={() => handleDeleteRow(index)}>Delete</button>
                                        </td>
                                    </tr>
                                ))}
                                <tr className='fs-5 text-center'>
                                    <td colSpan="2" className='text-start px-3'><strong>Total:</strong></td>
                                    <td><strong>{totalAmount}</strong></td>
                                    <td></td>
                                </tr>
                            </tbody>
                        </table>
                    </div>
                </div>
                <div className='row'>
                    <div className='col text-center'>
                        <button className='btn btn-primary' onClick={addRow}>
                            Add New Bill
                        </button>
                    </div>
                </div>
            </div>
        </>
    );
}

export default Sales;
