import { useState, useEffect } from 'react';
import { useNavigate } from 'react-router-dom';
import { fetchBillData, deleteBill, getBillById, renumberBills, updateElectronics, fetchElectronicsData } from './API';
import Swal from 'sweetalert2';
import './style.css';

function Sales() {
    const [arr, setArr] = useState([]);
    const [selectedDate, setSelectedDate] = useState(null);
    
    useEffect(() => {
        fetchBillData().then(res => setArr(res));
    }, []);

    const [electronics, setElectronics] = useState([]);
    useEffect(() => {
        fetchElectronicsData().then(res => setElectronics(res));
    }, []);

    const navigate = useNavigate();

    const addRow = () => {
        const billNo = arr.length + 1;
        navigate('/sales/bill/' + billNo);
    };

    const handleDeleteRow = async (id) => {
        // ... (existing delete logic)
    };

    const totalAmount = arr.reduce((acc, row) => acc + (parseFloat(row.Amount) || 0), 0);
    const totalProfit = arr.reduce((acc, row) => acc + (parseFloat(row.Profit) || 0), 0);

    const handleRowClick = async (id) => {
        await getBillById(id);
        navigate('/sales/' + id);
    };

    const filteredBills = selectedDate
        ? arr.filter(row => {
            let dateParts = row.date.split('-');
            let formattedDate = `${dateParts[2]}-${dateParts[1]}-${dateParts[0]}`;
            return formattedDate === selectedDate;
        })
        : arr;

    return (
        <>
            <div className='container-fluid'>
                <div className='header'>
                    <h1 className='text-center m-4' style={{ fontSize: "50px" }}>Sales</h1>
                    <div className='row'>
                        <div className='col my-4 mx-1 fs-5'>
                            Date: 
                            <span className='ms-2 align-middle'>
                                <input
                                    type='date'
                                    className='form-control d-inline-block w-auto dateCursor'
                                    value={selectedDate || ''} // Set value to selectedDate or empty string if null
                                    onChange={(e) => setSelectedDate(e.target.value)} // Update selectedDate on change
                                />
                            </span>
                            <button
                                className='btn btn-secondary ms-2'
                                onClick={() => setSelectedDate(null)} // Clear selected date
                            >
                                Clear Date
                            </button>
                        </div>
                    </div>
                </div>
                <div className='table-container'>
                    <table className='table table-striped table-hover table-bordered text-center tableSales'>
                        <thead>
                            <tr>
                                <th style={{ width: "3vw" }}>No.</th>
                                <th style={{ width: "5vw" }}>Date</th>
                                <th className='text-start' style={{ width: "29vw" }}>Customer</th>
                                <th style={{ width: "10vw" }}>Profit</th>
                                <th style={{ width: "10vw" }}>Amount</th>
                                <th style={{ width: "5vw" }}>Action</th>
                            </tr>
                        </thead>
                        <tbody className='align-middle'>
                            {filteredBills.map((row, index) => (
                                <tr key={index} onClick={() => handleRowClick(row.No)} style={{ cursor: 'pointer' }}>
                                    <td>{index + 1}</td>
                                    <td>{row.date}</td>
                                    <td className='text-start'>{row.CustomerName}</td>
                                    <td>{row.Profit}</td>
                                    <td>{row.Amount}</td>
                                    <td>
                                        <button className='btn btn-outline-danger border-2 fw-bold' onClick={(e) => {
                                            e.stopPropagation();
                                            handleDeleteRow(row.No);
                                        }}>
                                            Delete
                                        </button>
                                    </td>
                                </tr>
                            ))}
                            <tr className='fs-5 text-center'>
                                <td colSpan="3" className='text-start px-3'><strong>Total:</strong></td>
                                <td><strong>{totalProfit}</strong></td>
                                <td><strong>{totalAmount}</strong></td>
                                <td></td>
                            </tr>
                        </tbody>
                    </table>
                </div>
                <div className='row'>
                    <div className='col text-center'>
                        <button className='btn btn-primary' onClick={addRow}>
                            Add New Bill
                        </button>
                    </div>
                </div>
            </div>
        </>
    );
}

export default Sales;
