import './statementCss.css';
function Sales() {
    const today = new Date();
    const year = today.getFullYear();
    const month = String(today.getMonth() + 1).padStart(2, '0');
    const day = String(today.getDate()).padStart(2, '0');
    const formattedDate = day+'-'+month+'-'+year;

    const [rows, setRows] = useState([
        { No: '', Item: '', CostPrice: '', Quantity: '', SellPrice: '', Amount: '',Profit: '' }
    ]);

    const handleInputChange = (index, event) => {
        const { name, value } = event.target;
        const updatedRows = [...salesRows];
        updatedRows[index][name] = value;
        setSalesRows(updatedRows);
    };
    return (
        <>
            <h1 id='deSales' className='text-center m-3'>Sales</h1>
            <div className='container'>
                <div className='row'>
                    <div className='col'>Date: {formattedDate}</div>
                </div>
                <div className='row'>
                    <div className='col'>
                        <table className='table table-striped'>
                            <thead>
                                <tr>
                                    <th>No.</th>
                                    <th>Item</th>
                                    <th>Cost Price</th>
                                    <th>Quantity</th>
                                    <th>Sell Price</th>
                                    <th>Amount</th>
                                    <th>Profit</th>
                                </tr>
                            </thead>
                            <tbody>
                            {salesRows.map((row, index) => (
                                    <tr key={index}>
                                        <td>
                                            <input
                                                type='text'
                                                name='item'
                                                value={row.item}
                                                onChange={(e) => handleInputChange(index, e)}
                                                className='form-control'
                                                placeholder='Enter item'
                                            />
                                        </td>
                                        <td>
                                            <input
                                                type='number'
                                                name='quantity'
                                                value={row.quantity}
                                                onChange={(e) => handleInputChange(index, e)}
                                                className='form-control'
                                                placeholder='Enter quantity'
                                            />
                                        </td>
                                        <td>
                                            <input
                                                type='number'
                                                name='price'
                                                value={row.price}
                                                onChange={(e) => handleInputChange(index, e)}
                                                className='form-control'
                                                placeholder='Enter price'
                                            />
                                        </td>
                                    </tr>
                                ))}
                            </tbody>
                        </table>
                    </div>
                </div>
            </div>
        </>
    );
}

export default Sales;
