import './addStockCss.css';
import { useOutletContext } from 'react-router-dom';
import { useState } from 'react';
function AddStock() {
    const [Available, setAvailable] = useState(0);
    const [CostPrice, setCostPrice] = useState(0);
    const [selectedItem, setSelectedItem] = useState('');
    const [addOrDelete, setAddOrDelete] = useState('Add');
    const { arr, error } = useOutletContext();
    const [addNewItem, setAddNewItem] = useState(false);
    const handleItemChange = (e) => {
        const selectedItem = e.target.value;
        setSelectedItem(selectedItem);
        const selectedItemData = arr.find((item) => {
            return item.Item === selectedItem
        });
        if (selectedItemData) {
            setAvailable(selectedItemData.Available);
            setCostPrice(selectedItemData.CostPrice);
        }
        else {
            setAvailable(0);
            setCostPrice(0);
        }
    };
    const isButtonDisabled = !selectedItem && !addNewItem;
    const toggleNewItem = () => {
        setAddNewItem(!addNewItem);
        setSelectedItem('');
        setAvailable(0);
        setCostPrice(0);
    };
    return (
        <>
            <div className='container text-center d-block'>
                <div className='row'>
                    <div className='col'>
                        <h1 id='addDE'>Modify your Stock!</h1>
                    </div>
                </div>
                <div className='row justify-content-center'>
                    <div className='col-6 border border-dark'>
                        <form id='addStock'>
                            <div className='m-3'>
                                <label className='fs-4 me-3 align-middle'>Item: </label>
                                {error ? (
                                    <p>Error loading items:{error}</p>
                                ) : (
                                    <select value={selectedItem} onChange={handleItemChange} className='p-2 bg-light' role="button" disabled={addNewItem}>
                                        <option value=''>Select any item!</option>
                                        {arr.map((item) => (
                                            <option key={item.No}>
                                                {item.Item}
                                            </option>
                                        ))}
                                    </select>
                                )}
                            </div>
                            <div className='availableDiv text-start ms-3'>
                                <label>Available</label>
                                <div className='text-center fs-3'>{Available}</div>
                            </div>
                            <div className='cpDiv text-end me-3'>
                                <label>Cost Price</label>
                                <div className='text-center fs-3'>{CostPrice}</div>
                            </div>
                            <div className="btn-group btnAddDelete" role="group" aria-label="Basic radio toggle button group">
                                <input type="radio" className="btn-check" name="btnradio" id="Add" onChange={() => setAddOrDelete('Add')} checked={addOrDelete === 'Add'} disabled={isButtonDisabled} />
                                <label className="btn btn-outline-success px-4" htmlFor="Add">Add</label>
                                <input type="radio" className="btn-check" name="btnradio" id="Delete" onChange={() => setAddOrDelete('Delete')} checked={addOrDelete === 'Delete'} disabled={isButtonDisabled} />
                                <label className="btn btn-outline-danger px-3" htmlFor="Delete">Delete</label>
                            </div>
                            <div className='mt-4'>
                                <table className='m-auto w-60 user-select-none'>
                                    <tbody>
                                        <tr className='stockAdd'>
                                            <td className='text-start py-1'>
                                                <label className='fs-5 me-3 align-middle'>Item: </label>
                                            </td>
                                            <td>
                                                <input type='text' disabled={!addNewItem}/>
                                            </td>
                                        </tr>
                                        <tr className='stockAdd'>
                                            <td className='text-start py-1'>
                                                <label className='fs-5 me-3 align-middle'>No. of items to {addOrDelete}: </label>
                                            </td>
                                            <td>
                                                <input type='number' disabled={isButtonDisabled} />
                                            </td>
                                        </tr>
                                        <tr className='stockAdd'>
                                            <td className='text-start py-1'>
                                                <label className='fs-5 me-3 align-middle'>Cost Price: </label>
                                            </td>
                                            <td>
                                                <input type='number' disabled={!addNewItem}/>
                                            </td>
                                        </tr>
                                    </tbody>
                                </table>
                            </div>
                            <div className='m-4'>
                                <button className='btn btn-primary px-4' disabled={isButtonDisabled}>{addOrDelete}</button>
                            </div>
                        </form>
                        <div className='text-center my-4'>
                            <button className='btn btn-secondary' onClick={toggleNewItem}>
                                {addNewItem?'Update Existing Item':'Add New Item'}
                            </button>
                        </div>
                    </div>
                </div>
            </div>
        </>
    );
}

export default AddStock;
