import './addStockCss.css';
import { useOutletContext } from 'react-router-dom';
import { useState } from 'react';
function AddStock() {
    const { arr, error } = useOutletContext();
    return (
        <>
            <div className='container text-center'>
                <div className='row'>
                    <div className='col'>
                        <h1 id='addDE'>Modify your Stock!</h1>
                    </div>
                </div>
                <div className='row justify-content-center'>
                    <div className='col-6'>
                        <form id='addStock'>
                            <div className='m-3'>
                                <label>Item: </label>
                                {error ? (
                                    <p>Error loading items: {error}</p>
                                ) : (
                                    <select>
                                        <option>Select any item!</option>

                                        {arr.map((item) => (
                                            <option key={item.id} value={item.no}>
                                                {item.item}
                                            </option>
                                        ))}
                                    </select>
                                )}
                            </div>
                            <div>
                                <label>Available: </label>
                                <input type='text' />
                            </div>
                        </form>
                    </div>
                </div>
            </div>
        </>
    );
}

export default AddStock;
