const apiURL = 'https://667323296ca902ae11b33da7.mockapi.io/Electronics';
const api='http://localhost:3000';
export const fetchElectronicsData=()=>{
    const arr=fetch(api+'/stock').then(res=>res.json());
    return arr;
}
export const addElectronics=(data,navigate)=>{
    fetch(api+'/modifyStock',{
        method:"POST",
        body:JSON.stringify(data),
        headers:{
            "Content-Type":"application/json"
        }
    })
    .then(res=>res.json())
    .then(res=>{
        navigate('/stock');
    });
}
export const editElectronics=(data,id,navigate)=>{
    fetch(api+'/modifyStock/'+id,{
        method:"PUT",
        body:JSON.stringify(data),
        headers:{
            "Content-Type":"application/json"
        }
    })
    .then(res=>res.json())
    .then(res=>{
        navigate('/stock');
    });
}
export const deleteElectronics = async (id) => {    
    fetch(api+'/stock/'+id, 
        {method:"DELETE"}
    )
    .then(res=>res.json());
}
export const renumberElectronics = async (id) => {
    fetch('/api/renumberElectronics', {
        method: 'POST',
        body: JSON.stringify({ deletedNo: id }),
        headers: {
            'Content-Type': 'application/json'
        }
    })
    .then(response => response.json())
    .then(updatedData => {
        // Step 3: Update the frontend with the renumbered items
        setArr(updatedData);
    });
}



