import './statementCss.css';
import { useOutletContext } from 'react-router-dom';
import {useState} from 'react';
function Sales() {
    const today = new Date();
    const year = today.getFullYear();
    const month = String(today.getMonth() + 1).padStart(2, '0');
    const day = String(today.getDate()).padStart(2, '0');
    const formattedDate = day+'-'+month+'-'+year;

    const [rows, setRows] = useState([
        { No: '', Item: '', CostPrice: '', Quantity: '', SellPrice: '', Amount: '',Profit: '' }
    ]);

    const handleInputChange = (index, e) => {
        const { name, value } = e.target;
        const updatedRows = [...rows];
        updatedRows[index][name] = value;

        const quantity = updatedRows[index].Quantity || 0;
        const sellPrice = updatedRows[index].SellPrice || 0;
        const costPrice = updatedRows[index].CostPrice || 0;

        updatedRows[index].Amount = sellPrice * quantity;
        updatedRows[index].Profit = (sellPrice - costPrice) * quantity;

        setRows(updatedRows);
    };

    const [no, setNo] = useState(1);
    const [selectedItem, setSelectedItem] = useState('');
    const { arr, error } = useOutletContext();

    const handleItemChange = (index,e) => {
        const selectedItem = e.target.value;
        const selectedItemData = arr.find((item) => item.Item === selectedItem);

        const updatedRows = [...rows];
        updatedRows[index].Item = selectedItem;
        updatedRows[index].CostPrice = selectedItemData ? selectedItemData.CostPrice : '';

        setRows(updatedRows);
    };

    const addRow = () => {
        setRows([...rows, { Item: '', CostPrice: '', Quantity: '', SellPrice: '', Amount: '', Profit: '' }]);
    };

    return (
        <>
            <h1 id='deSales' className='text-center m-3'>Sales</h1>
            <div className='container'>
                <div className='row'>
                    <div className='col'>Date: {formattedDate}</div>
                </div>
                <div className='row'>
                    <div className='col'>
                        <table className='table table-striped'>
                            <thead>
                                <tr>
                                    <th>No.</th>
                                    <th>Item</th>
                                    <th>Cost Price</th>
                                    <th>Quantity</th>
                                    <th>Sell Price</th>
                                    <th>Amount</th>
                                    <th>Profit</th>
                                </tr>
                            </thead>
                            <tbody>
                            {rows.map((row, index) => (
                                    <tr key={index}>
                                        <td className='align-middle'>
                                            {index + 1}
                                        </td>
                                        <td>
                                        <select value={selectedItem} onChange={handleItemChange} className='p-2 bg-light' role="button">
                                        <option value=''>Select any item!</option>
                                        {arr.map((item) => (
                                            <option key={item.No}>
                                                {item.Item}
                                            </option>
                                        ))}
                                    </select>
                                        </td>
                                        <td className='align-middle'>
                                            {row.CostPrice}
                                        </td>
                                        <td>
                                            <input
                                                type='number'
                                                name='row.Quantity'
                                                value={row.Quantity}
                                                onChange={(e) => handleInputChange(index, e)}
                                                className='form-control'
                                                placeholder='Enter quantity'
                                            />
                                        </td>
                                        <td>
                                            <input
                                                type='number'
                                                name='price'
                                                value={row.SellPrice}
                                                onChange={(e) => handleInputChange(index, e)}
                                                className='form-control'
                                                placeholder='Enter price'
                                            />
                                        </td>
                                        <td>
                                            {row.Amount}
                                        </td>
                                        <td>
                                            {row.Profit}
                                        </td>
                                    </tr>
                                ))}
                            </tbody>
                        </table>
                    </div>
                </div>
                <div className='row'>
                    <div className='col text-center'>
                        <button className='btn btn-primary' onClick={addRow}>
                            Add New Row
                        </button>
                    </div>
                </div>
            </div>
        </>
    );
}

export default Sales;
