import React from 'react';
import ReactDOM from 'react-dom/client';
import './index.css';
import App from './App';
import reportWebVitals from './reportWebVitals';
import { BrowserRouter, Routes, Route } from 'react-router-dom';
import Home from './components/Home.js';
import Stock from './components/Stock.js';
import AddStock from './components/ModifyStock.js';
import Sales from './components/Sales';
import Bill from './components/Bill';
import Bill_id from './components/Bill_id';
import { useEffect } from 'react';
import { useLocation } from 'react-router-dom';

const root = ReactDOM.createRoot(document.getElementById('root'));
function useScrollToTop() {
  const location = useLocation();

  useEffect(() => {
      window.scrollTo(0, 0);
  }, [location]);
}
root.render(
  <BrowserRouter>
    <Routes>
      <Route path='/' element={<App />}>
        <Route path='/' element={<Home />} />
        <Route path='/modifyStock' element={<AddStock />} />
        <Route path='/modifyStock/:id' element={<AddStock />} />
        <Route path='/stock' element={<Stock />} />
        <Route path='/sales' element={<Sales />} />
        <Route path='/sales/bill/:No' element={<Bill />} />
        <Route path='/sales/:id' element={<Bill_id />} />
      </Route>
    </Routes>
  </BrowserRouter>
);

// If you want to start measuring performance in your app, pass a function
// to log results (for example: reportWebVitals(console.log))
// or send to an analytics endpoint. Learn more: https://bit.ly/CRA-vitals
reportWebVitals();
