import './stockCss.css';
import {useState,useEffect} from 'react';
function Stock(){
    const [arr,setArr]=useState([]);
    let apiURL='https://667323296ca902ae11b33da7.mockapi.io/Electronics';
    useEffect(()=>{
        fetch(apiURL)
            .then((res)=>{
                if(!res.ok){
                    throw new Error('Network Response Error');
                }
                return res.json();
            })
            .then((data)=>{
                console.log("API Called");
                setArr(data);
                console.log(arr);
            })
            .catch((err)=>{
                throw new Error('API Response Error');
            })
    },[]);
    return(
        <>
            <h1>Dariyalal Electronics</h1>
            <div className='container'>
                <div className='row'>
                    <div className='col'>
                        <h3>No.</h3>
                    </div>
                    <div className='col'>
                        <h3>Item</h3>
                    </div>
                    <div className='col'>
                        <h3>Available</h3>
                    </div>
                    <div className='col'>
                        <h3>Cost Price</h3>
                    </div>
                    <div className='col'>
                        <h3>Selling Price</h3>
                    </div>
                </div>
                {arr.map((item,index)=>{
                    <div className='row' key={item.id}>
                        <div className='col'>
                            <h3>{index+1}</h3>
                        </div>
                        <div className='col'>
                            <h3>{item.Item}</h3>
                        </div>
                        <div className='col'>
                            <h3>{item.Available}</h3>
                        </div>
                        <div className='col'>
                            <h3>Cost Price</h3>
                        </div>
                        <div className='col'>
                            <h3>Selling Price</h3>
                        </div>
                    </div>
                })}
            </div>
        </>
    );
}

export default Stock;
