import './stockCss.css';
function Stock(){
    const { arr, error } = useOutletContext();
    // const [arr, setArr] = useState([]);
    // const [error, setError] = useState(null);

    // useEffect(() => {
    //     const fetchData = async () => {
    //         try {
    //             const data = await fetchElectronicsData();
    //             setArr(data);
    //         } catch (err) {
    //             setError(err.message);
    //         }
    //     };

    //     fetchData();
    // }, []);
    return(
        <>
            <h1>Dariyalal Electronics</h1>
            <div className='container'>
                <div className='row'>
                    <div className='col'>
                        {error?(
                            <p>Error: {error}</p>
                        ):(
                            <table border="1" className='table  table-hover table-info table-bordered'>
                                <thead>
                                    <tr border="2">
                                        <th id='thNO'>No.</th>
                                        <th>Item</th>
                                        <th className='price'>Cost Price</th>
                                        <th id='thAvailable'>Available</th>
                                        <th className='price'>Selling Price</th>
                                    </tr>
                                </thead>
                                <tbody>
                                    {data.map((item,index)=>(
                                        <tr key={item.id}>
                                            <td>{index+1}</td>
                                            <td>{item.Item}</td>
                                            <td>{item.CostPrice}</td>
                                            <td>{item.SellPrice}</td>
                                            <td>{item.Available}</td>
                                        </tr>
                                    ))}
                                </tbody>
                            </table>
                        )}
                    </div>
                </div>    
            </div>
        </>
    );
}

export default Stock;
