import './stockCss.css';
function Stock(){
    const [arr,setArr]=useState([]);
    const [error, setError] = useState(null);
    let apiURL='https://667323296ca902ae11b33da7.mockapi.io/Electronics';
    useEffect(()=>{
        fetch(apiURL)
            .then((res)=>{
                if(!res.ok){
                    throw new Error('Network Response Error');
                }
                return res.json();
            })
            .then((data)=>{
                console.log("API Called");
                setArr(data);
                console.log(arr);
            })
            .catch((err)=>{
                throw new Error('API Response Error');
            })
    },[]);
    return(
        <>
            <h1>Dariyalal Electronics</h1>
            <div className='container'>
                <div className='row'>
                    <div className='col'>
                        {error?(
                            <p>Error: {error}</p>
                        ):(
                            <table border="1" className='table  table-hover table-info table-bordered'>
                                <thead>
                                    <tr border="2">
                                        <th id='thNO'>No.</th>
                                        <th>Item</th>
                                        <th className='price'>Cost Price</th>
                                        <th id='thAvailable'>Available</th>
                                        <th className='price'>Selling Price</th>
                                    </tr>
                                </thead>
                                <tbody>
                                    {arr.map((item,index)=>(
                                        <tr key={item.id}>
                                            <td>{index+1}</td>
                                            <td>{item.Item}</td>
                                            <td>{item.CostPrice}</td>
                                            <td>{item.SellPrice}</td>
                                            <td>{item.Available}</td>
                                        </tr>
                                    ))}
                                </tbody>
                            </table>
                        )}
                    </div>
                </div>    
            </div>
        </>
    );
}

export default Stock;
