import './addStockCss.css';
import { useOutletContext } from 'react-router-dom';
import { useState } from 'react';
function AddStock() {
    const [Available,setAvailable]=useState(0);
    const {arr,error}=useOutletContext();
    const handleItemChange=(e)=>{
        setSelectedItem(e.target.value);
    };
    return (
        <>
            <div className='container text-center'>
                <div className='row'>
                    <div className='col'>
                        <h1 id='addDE'>Modify your Stock!</h1>
                    </div>
                </div>
                <div className='row justify-content-center'>
                    <div className='col-6'>
                        <form id='addStock'>
                            <div className='m-3'>
                                <label>Item: </label>
                                {error?(
                                    <p>Error loading items:{error}</p>
                                ):(
                                    <select>
                                        <option>Select any item!</option>
                                        {arr.map((item) => (
                                            <option key={item.No}>
                                                {item.Item}
                                            </option>
                                        ))}
                                    </select>
                                )}
                            </div>
                            <div>
                                <label>Available: </label>
                                <div>{Available}</div>
                            </div>
                        </form>
                    </div>
                </div>
            </div>
        </>
    );
}

export default AddStock;
