const apiURL = 'https://667323296ca902ae11b33da7.mockapi.io/Electronics';
const api='http://localhost:3000';
export const fetchElectronicsData=()=>{
    const arr=fetch(api+'/stock').then(res=>res.json());
    return arr;
}
export const addElectronics=(data,navigate)=>{
    fetch(api+'/modifyStock',{
        method:"POST",
        body:JSON.stringify(data),
        headers:{
            "Content-Type":"application/json"
        }
    })
    .then(res=>res.json())
    .then(res=>{
        navigate('/stock');
    });
}
export const editElectronics=(data,id,navigate)=>{
    fetch(api+'/modifyStock/'+id,{
        method:"PUT",
        body:JSON.stringify(data),
        headers:{
            "Content-Type":"application/json"
        }
    })
    .then(res=>res.json())
    .then(res=>{
        navigate('/stock');
    });
}
export const deleteElectronics = async (id) => {    
    fetch(apiURL+'/'+id, {method: "DELETE"})
    .then(res=>res.json());
};


const apiBill="https://667323296ca902ae11b33da7.mockapi.io/BillData";
export const fetchBillData=()=>{
    const arr=fetch(apiBill).then(res=>res.json());
    return arr;
}
export const addBill=(data,navigate)=>{
    fetch(apiBill,{
        method:"POST",
        body:JSON.stringify(data),
        headers:{
            "Content-Type":"application/json"
        }
    })
    .then(res=>res.json())
    .then(res=>{
        navigate('/sales');
    });
}
export const deleteBill = async (id,navigate) => {    
    fetch(apiBill+'/'+id, {method: "DELETE"})
    .then(res=>res.json())
    .then(res=>{
        navigate('/sales');
    });
};
export const getBillById=async (id)=>{
    const res=await fetch(apiBill+'/'+id,{method:"GET"});
    const data=await res.json();
    return data;
};
