import './style.css';
import { getBillById } from './API';
import { useState, useEffect } from 'react';
import { useParams,Link } from 'react-router-dom';
function Bill_id() {
    const { id } = useParams();
    const [bill, setBill] = useState({ 
                                date: '', 
                                CustomerName: '', 
                                ItemNames: [],
                                Quantities: [],
                                Price: [], 
                                Amount: 0 
                            });
    useEffect(() => {
        getBillById(id)
            .then(bill => {
                setBill(bill)
            });
    }, [id]);

    return (
        <>
            <h1 className='text-center m-4' style={{ fontSize: "50px" }}>Bill</h1>
            <div className='container'>
                <div className='row'>
                    <div className='col mb-4 ms-4 p-2'>
                        <Link to='/sales' className='fs-5 backToSales'>Back to Sales</Link>
                    </div>
                </div>
                <div className='row justify-content-center'>
                    <div className='col fs-5'>
                        Date: <span>{bill.date}</span>
                    </div>
                    <div className='col fs-5'>
                        Name: <span className='fs-4'>{bill.CustomerName}</span>
                    </div>
                </div>
            </div>
            <div className='container d-flex justify-content-center align-items-center'>
                <div className='row'>
                    <div className='col-12'>
                        <table border="1" className='table table-hover table-info table-bordered text-center'>
                            <thead>
                                <tr border="2">
                                    <th style={{width: "3vw"}}>No.</th>
                                    <th style={{width: "30vw"}}>Item</th>
                                    <th style={{width: "10vw"}}>Quantity</th>
                                    <th style={{width: "10vw"}}>Price</th>
                                    <th style={{width: "10vw"}}>Amount</th>
                                </tr>
                            </thead>
                            <tbody>
                            {bill.ItemNames.length > 0 ? (
                                    bill.ItemNames.map((itemName, index) => (
                                        <tr key={index}>
                                            <td>{index + 1}</td>
                                            <td>{itemName}</td>
                                            <td>{bill.Quantities[index]}</td>
                                            <td>{bill.Price[index]}</td>
                                            <td>{(bill.Quantities[index] * bill.Price[index])}</td>
                                        </tr>
                                    ))
                                ) : (
                                    <tr>
                                        <td colSpan="5">No items in the bill</td>
                                    </tr>
                                )}
                            </tbody>
                        </table>
                    </div>
                </div>
            </div>
        </>
    );
}

export default Bill_id;
