import './style.css';
import { useNavigate } from 'react-router-dom';
import { useState, useRef, useEffect } from 'react';
import { deleteElectronics, fetchElectronicsData, editElectronics } from './API';
import { addElectronics } from './API';

function AddStock() {
    const [arr, setArr] = useState([]);
    useEffect(() => {
        fetchElectronicsData()
            .then(res => setArr(res));
    }, []);
    const [itemDetails, setItemDetails] = useState({
        available: 0,
        costPrice: 0,
        id: -1,
        selectedItem: '',
        addOrDelete: 'Add',
        addNewItem: false,
        newItemName: '',
        newItemCount: '',
        newItemCostPrice: ''
    });
    

    const addButtonRef = useRef(null);

    // const handleItemChange = (e) => {
    //     const selectedItem = e.target.value;
    //     setSelectedItem(selectedItem);
    //     const selectedItemData = arr.find((item) => {
    //         return item.Item === selectedItem
    //     });
    //     if (selectedItemData) {
    //         setID(selectedItemData.id);
    //         navigate('/'+id);
    //         setNewItemName(selectedItemData.Item);
    //         setNewItemCostPrice(selectedItemData.CostPrice);
    //         setAvailable(selectedItemData.Available);
    //         setCostPrice(selectedItemData.CostPrice);
    //     }
    //     else {
    //         setAvailable(0);
    //         setCostPrice(0);
    //     }
    // };
    const handleItemChange = (e) => {
        const selectedItem = e.target.value;
        const selectedItemData = arr.find((item) => item.Item === selectedItem);
    
        if (selectedItemData) {
            setItemDetails(prev => ({
                ...prev,
                id: selectedItemData.id,
                selectedItem,
                available: selectedItemData.Available,
                costPrice: selectedItemData.CostPrice,
                newItemName: selectedItemData.Item
            }));
        } else {
            setItemDetails(prev => ({
                ...prev,
                available: 0,
                costPrice: 0,
                selectedItem,
                id: -1
            }));
        }
    };
    

    const isButtonDisabled = !selectedItem && !addNewItem;

    const toggleNewItem = () => {
        setAddNewItem(!addNewItem);
        setSelectedItem('');
        setAvailable(0);
        setCostPrice(0);
        setNewItemName('');
        setNewItemCount('');
        setNewItemCostPrice('');
        setAddOrDelete('Add');
    };

    const handleKeyPress = (e) => {
        if (e.key === 'Enter') {
            e.preventDefault();
            if (addButtonRef.current && !isButtonDisabled) {
                addButtonRef.current.click();
            }
        }
    };
    const navigate = useNavigate();
    // const handleFormSubmit = async (e) => {
    //     e.preventDefault();
    //     const stockData = {
    //         Item: newItemName,
    //         Available: parseInt(newItemCount),
    //         CostPrice: parseInt(newItemCostPrice)
    //     };
    //     if (selectedItem === '') {
    //         if (addOrDelete === 'Add') {
    //             addElectronics(stockData, navigate);
    //             setNewItemName('');
    //             setNewItemCount('');
    //             setNewItemCostPrice('');
    //         }
    //         else {
    //             deleteElectronics(id, navigate);
    //         }
    //     }
    //     else{
    //         editElectronics(stockData, navigate);
    //     }
    // }
    const handleFormSubmit = async (e) => {
        e.preventDefault();
        const stockData = {
            Item: itemDetails.newItemName,
            Available: parseInt(itemDetails.newItemCount),
            CostPrice: parseInt(itemDetails.newItemCostPrice)
        };
    
        try {
            if (itemDetails.selectedItem === '') {
                if (itemDetails.addOrDelete === 'Add') {
                    await addElectronics(stockData, navigate);
                } else {
                    if (itemDetails.id !== -1) {
                        await deleteElectronics(itemDetails.id, navigate);
                    }
                }
            } else {
                await editElectronics(stockData, navigate);
            }
    
            // Clear form after submission
            setItemDetails(prev => ({
                ...prev,
                newItemName: '',
                newItemCount: '',
                newItemCostPrice: ''
            }));
        } catch (error) {
            console.error("Error processing stock update:", error);
            // Optionally show an error message to the user
        }
    };
    

    return (
        <>
            <div className='container text-center d-block'>
                <div className='row'>
                    <div className='col'>
                        <h1 id='addDE'>Modify your Stock!</h1>
                    </div>
                </div>
                <div className='row justify-content-center text-white'>
                    <div className='col-lg-6 shadow rounded-4 p-3' style={{ backgroundColor: "#9e0671" }}>
                        <form onKeyDown={handleKeyPress}>
                            <div className='m-3'>
                                <label className='fs-4 me-3 align-top'>Item: </label>
                                <select value={selectedItem}
                                    onChange={handleItemChange}
                                    className='p-2 bg-light'
                                    role="button"
                                    disabled={addNewItem}>
                                    <option value=''>Select any item!</option>
                                    {arr.map((item) => (
                                        <option key={item.No}>
                                            {item.Item}
                                        </option>
                                    ))}
                                </select>
                            </div>
                            <div className='availableDiv text-start ms-3'>
                                <label>Available</label>
                                <div className='text-center fs-3'>{Available}</div>
                            </div>
                            <div className='cpDiv text-end me-3'>
                                <label>Cost Price</label>
                                <div className='text-center fs-3'>{CostPrice}</div>
                            </div>
                            <div className="btn-group btnAddDelete" role="group">
                                <input
                                    type="radio"
                                    className="btn-check"
                                    name="btnradio"
                                    id="Add"
                                    onChange={() => setAddOrDelete('Add')}
                                    checked={addOrDelete === 'Add'}
                                    disabled={isButtonDisabled}
                                />
                                <label
                                    className="btn btn-outline-success px-4 border-3"
                                    style={{ color: 'white' }}
                                    htmlFor="Add"
                                >Add
                                </label>
                                {/* <input
                                    type="radio"
                                    className="btn-check"
                                    name="btnradio"
                                    id="Delete"
                                    onChange={() => setAddOrDelete('Delete')}
                                    checked={addOrDelete === 'Delete'}
                                    disabled={isButtonDisabled || addNewItem}
                                /> */}
                                <input
    type="radio"
    className="btn-check"
    name="btnradio"
    id="Add"
    onChange={() => setItemDetails(prev => ({ ...prev, addOrDelete: 'Add' }))}
    checked={itemDetails.addOrDelete === 'Add'}
    disabled={isButtonDisabled}
/>

                                <label
                                    className="btn btn-outline-danger px-3 border-3"
                                    style={{ color: 'white' }}
                                    htmlFor="Delete"
                                >Delete
                                </label>
                            </div>
                            <div className='mt-4'>
                                <table className='m-auto w-60 user-select-none'>
                                    <tbody>
                                        <tr className='stockAdd'>
                                            <td className='text-start py-1'>
                                                <label className='fs-5 me-3 align-middle'>Item: </label>
                                            </td>
                                            <td>
                                                {/* <input
                                                    type='text'
                                                    disabled={!addNewItem}
                                                    value={newItemName}
                                                    onChange={(e) => setNewItemName(e.target.value)}
                                                /> */}
                                                <input
    type='text'
    disabled={!itemDetails.addNewItem}
    value={itemDetails.newItemName}
    onChange={(e) => setItemDetails(prev => ({ ...prev, newItemName: e.target.value }))}
/>

                                            </td>
                                        </tr>
                                        <tr className='stockAdd'>
                                            <td className='text-start py-1'>
                                                <label className='fs-5 me-3 align-middle'>No. of items to {addOrDelete}: </label>
                                            </td>
                                            <td>
                                                <input
                                                    type='number'
                                                    disabled={isButtonDisabled}
                                                    value={newItemCount}
                                                    onChange={(e) => setNewItemCount(e.target.value)}
                                                />
                                            </td>
                                        </tr>
                                        <tr className='stockAdd'>
                                            <td className='text-start py-1'>
                                                <label className='fs-5 me-3 align-middle'>Cost Price: </label>
                                            </td>
                                            <td>
                                                <input
                                                    type='number'
                                                    disabled={!addNewItem}
                                                    value={newItemCostPrice}
                                                    onChange={(e) => setNewItemCostPrice(e.target.value)}
                                                />
                                            </td>
                                        </tr>
                                    </tbody>
                                </table>
                            </div>
                            <div className='m-4'>
                                <button className='btn btn-primary px-4'
                                    disabled={isButtonDisabled}
                                    ref={addButtonRef}
                                    onClick={handleFormSubmit}>
                                    {addOrDelete}
                                </button>
                            </div>
                        </form>
                        <div className='text-center my-4'>
                            <button className='btn btn-secondary' onClick={toggleNewItem}>
                                {addNewItem ? 'Update Existing Item' : 'Add New Item'}
                            </button>
                        </div>
                    </div>
                </div>
            </div>
        </>
    );
}

export default AddStock;
