import './addStockCss.css';
import { useOutletContext } from 'react-router-dom';
import { useState, useEffect } from 'react';
function AddStock() {
    const [Available, setAvailable] = useState(0);
    const [CostPrice, setCostPrice] = useState(0);
    const [selectedItem, setSelectedItem] = useState('');
    const [addOrDelete, setAddOrDelete] = useState('Add');
    const [stockChange, setStockChange] = useState(0);
    const { arr, error } = useOutletContext();
    const handleItemChange = (e)=>{
        const selectedItem = e.target.value;
        setSelectedItem(selectedItem);
        const selectedItemData=arr.find(item => item.Item === selectedItem);
        if (selectedItemData) {
            setAvailable(selectedItemData.Available || 0);
            setCostPrice(selectedItemData.CostPrice || 0);
        }
        else {
            setAvailable(0);
            setCostPrice(0);
        }
    };
    const handleStockChange = (e)=>{
        setStockChange(Number(e.target.value));
    };
    const handleFormSubmit = (e)=>{
        e.preventDefault();
        if(addOrDelete === 'Add'){
            setAvailable(Available + stockChange);
        }
        else if(addOrDelete === 'Delete'){
            setAvailable(Math.max(0,Available-stockChange));
        }
    };
    return (
        <>
            <div className='container text-center'>
                <div className='row'>
                    <div className='col'>
                        <h1 id='addDE'>Modify your Stock!</h1>
                    </div>
                </div>
                <div className='row justify-content-center'>
                    <div className='col-6 border border-dark'>
                        <form id='addStock'>
                            <div className='m-3'>
                                <label className='fs-4 me-3 align-middle'>Item: </label>
                                {error ? (
                                    <p>Error loading items:{error}</p>
                                ) : (
                                    <select value={selectedItem} onChange={handleItemChange} className='p-2 bg-light' role="button">
                                        <option>Select any item!</option>
                                        {arr.map((item) => (
                                            <option key={item.No}>
                                                {item.Item}
                                            </option>
                                        ))}
                                    </select>
                                )}
                            </div>
                            <div className='availableDiv text-start ms-3'>
                                <label>Available</label>
                                <div className='text-center fs-3'>{Available}</div>
                            </div>
                            <div className='cpDiv text-end me-3'>
                                <label>Cost Price</label>
                                <div className='text-center fs-3'>{CostPrice}</div>
                            </div>
                            <div className="btn-group btnAddDelete" role="group" aria-label="Basic radio toggle button group">
                                <input type="radio" className="btn-check" name="btnradio" id="Add" autocomplete="off" onChange={()=>setAddOrDelete('Add')} checked={addOrDelete === 'Add'} />
                                <label className="btn btn-outline-success px-4" for="Add">Add</label>
                                <input type="radio" className="btn-check" name="btnradio" id="Delete" autocomplete="off" onChange={()=>setAddOrDelete('Delete')} checked={addOrDelete === 'Delete'}/>
                                <label className="btn btn-outline-danger px-3" for="Delete">Delete</label>
                            </div>
                            <div className='stockAdd mt-4 mb-4'>
                                <label className='fs-5 me-3 align-middle'>No. of items to {addOrDelete}: </label>
                                <input type='number' value={stockChange} />
                            </div>
                            <div className='m-4'>
                                <button className='btn btn-primary px-4' onClick={handleStockChange}>{addOrDelete}</button>
                            </div>
                        </form>
                    </div>
                </div>
            </div>
        </>
    );
}

export default AddStock;
